import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../stylesheets/deletecar.css'

const DeleteCar = () => {
  const { id } = useParams(); // Destructure 'id' from the route parameters
  const navigate = useNavigate();

  const [carData, setCarData] = useState({
    Name: '',
    Type: '',
    Make: '',
    Price: 0,
    Image: '',
    Desc: '',
    Fuel: '',
    Year: '',
  });

  useEffect(() => {
    // Fetch car data based on 'id' from the server and update state
    // Replace the URL with your API endpoint
    fetch(`/api/cars/${id}`)
      .then((response) => response.json())
      .then((data) => setCarData(data))
      .catch((error) => console.error('Error fetching car data:', error));
  }, [id]); // Include 'id' in the dependency array

  const handleDelete = () => {
    // Perform logic to delete the car data in the database
    // Replace the URL with your API endpoint
    fetch(`/api/cars/${id}`, {
      method: 'DELETE',
    })
      .then((response) => {
        if (response.ok) {
          console.log('Car deleted successfully');
          window.alert("Car deleted successfully!");
          navigate('/');
        } else {
          console.error('Error deleting car:', response.statusText);
        }
      })
      .catch((error) => console.error('Error deleting car:', error));
  };

  return (
    <div>
      <h2>Delete Car</h2>
      <p>Are you sure you want to delete the car "{carData.Name}"?</p>
      <button onClick={handleDelete}>Delete</button>
    </div>
  );
};

export default DeleteCar;
