import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const ModifyCar = () => {
  const navigate = useNavigate();

  // State to store the car name entered by the user
  const [carName, setCarName] = useState('');

  // State to store the car data fetched based on the entered name
  const [carData, setCarData] = useState({
    Name: '',
    Type: '',
    Make: '',
    Price: 0,
    Image: '',
    Desc: '',
    FuelType: '',
    LaunchDate: 0,
    Acceleration: '',
  });

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform logic to update the car data in the database
    // ...

    console.log('Car data modified:', carData);
    window.alert('Car details modified successfully!');
    navigate('/admin');
  };

  // Function to handle changes in the car name input
  const handleNameChange = (e) => {
    setCarName(e.target.value);
  };

  // Function to fetch car data based on the entered name
  const fetchCarData = async () => {
    try {
      const response = await fetch(`/api/cars/${carName}`);
      if (!response.ok) {
        throw new Error(`Error fetching car data: ${response.statusText}`);
      }

      const data = await response.json();
      setCarData(data);
    } catch (error) {
      console.error(error);
      // Handle error, e.g., display an error message to the user
    }
  };

  // Fetch car data whenever the car name changes
  useEffect(() => {
    fetchCarData();
  }, [carName]);

  return (
    <div>
      <h2>Modify Car</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Enter Car Name:
            <input type="text" value={carName} onChange={handleNameChange} />
          </label>
          <button type="button" onClick={fetchCarData}>
            Search
          </button>
        </div>

        {/* Display car data fields for modification */}
        {/* ... (same as your existing code) */}

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default ModifyCar;
