import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const ModifyCar = () => {
  const { id } = useParams(); // Destructure 'id' from the route parameters

  const [carData, setCarData] = useState({
    Name: '',
    Type: '',
    Make: '',
    Price: 0,
    Image: '',
    Desc: '',
    Fuel: '',
    Year: '',
  });

  useEffect(() => {
    // Fetch car data based on 'id' from the server and update state
    // Replace the URL with your API endpoint
    fetch(`/api/cars/${id}`)
      .then((response) => response.json())
      .then((data) => setCarData(data))
      .catch((error) => console.error('Error fetching car data:', error));
  }, [id]); // Include 'id' in the dependency array

  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform logic to update the car data in the database
    // ...

    console.log('Car data modified:', carData);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCarData((prevData) => ({ ...prevData, [name]: value }));
  };

  return (
    <div>
      <h2>Modify Car</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Name:
            <input type="text" name="Name" value={carData.Name} onChange={handleChange} required />
          </label>
        </div>
        <div>
          <label>
            Type:
            <input type="text" name="Type" value={carData.Type} onChange={handleChange} required />
          </label>
        </div>
        <div>
          <label>
            Make:
            <input type="text" name="Make" value={carData.Make} onChange={handleChange} required />
          </label>
        </div>
        <div>
          <label>
            Price:
            <input type="number" name="Price" value={carData.Price} onChange={handleChange} required />
          </label>
        </div>
        <div>
          <label>
            Image:
            <input type="text" name="Image" value={carData.Image} onChange={handleChange} required />
          </label>
        </div>
        <div>
          <label>
            Description:
            <textarea name="Desc" value={carData.Desc} onChange={handleChange} required />
          </label>
        </div>
        <div>
          <label>
            Fuel:
            <input type="text" name="Fuel" value={carData.Fuel} onChange={handleChange} required />
          </label>
        </div>
        <div>
          <label>
            Year:
            <input type="text" name="Year" value={carData.Year} onChange={handleChange} required />
          </label>
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default ModifyCar;
